document.addEventListener("DOMContentLoaded",function(){const l=document.querySelectorAll(".navbar-burger"),t=document.querySelectorAll(".navbar-menu");if(l.length&&t.length)for(var e=0;e<l.length;e++)l[e].addEventListener("click",function(){for(var d=0;d<t.length;d++)t[d].classList.toggle("hidden")});const r=document.querySelectorAll(".navbar-close"),o=document.querySelectorAll(".navbar-backdrop"),c=document.querySelectorAll(".close-menu");if(c.length)for(var e=0;e<c.length;e++)c[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.add("hidden")});if(r.length)for(var e=0;e<r.length;e++)r[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.toggle("hidden")});if(o.length)for(var e=0;e<o.length;e++)o[e].addEventListener("click",function(){for(var n=0;n<t.length;n++)t[n].classList.toggle("hidden")})});
