document.addEventListener("DOMContentLoaded",function(){let a=document.getElementsByClassName("accordion"),s;for(s=0;s<a.length;s++)a[s].addEventListener("click",function(){this.classList.toggle("active");let l=this.querySelector("svg.opened"),t=this.querySelector("svg.closed");l.classList.toggle("hidden"),t.classList.toggle("hidden");let c=this.nextElementSibling;c.style.maxHeight?c.style.maxHeight=null:c.style.maxHeight=c.scrollHeight+"px"});const o=document.querySelectorAll(".navbar-burger"),n=document.querySelectorAll(".navbar-menu");if(o.length&&n.length)for(var e=0;e<o.length;e++)o[e].addEventListener("click",function(){for(var l=0;l<n.length;l++)n[l].classList.toggle("hidden")});const i=document.querySelectorAll(".navbar-close"),r=document.querySelectorAll(".navbar-backdrop");if(i.length)for(var e=0;e<i.length;e++)i[e].addEventListener("click",function(){for(var t=0;t<n.length;t++)n[t].classList.toggle("hidden")});if(r.length)for(var e=0;e<r.length;e++)r[e].addEventListener("click",function(){for(var t=0;t<n.length;t++)n[t].classList.toggle("hidden")})});
